package com.pedidos.reservadegrass.data;public class ObjetoPerdidodata {	private String nombre;	private String color;	private String marca;	private String  lugarpedida;	private String fotoobjeto;	public ObjetoPerdidodata() {	}	public String getNombre() {		return nombre;	}	public void setNombre(String nombre) {		this.nombre = nombre;	}	public String getColor() {		return color;	}	public void setColor(String color) {		this.color = color;	}	public String getMarca() {		return marca;	}	public void setMarca(String marca) {		this.marca = marca;	}	public String getLugarpedida() {		return lugarpedida;	}	public void setLugarpedida(String lugarpedida) {		this.lugarpedida = lugarpedida;	}	public String getFotoobjeto() {		return fotoobjeto;	}	public void setFotoobjeto(String fotoobjeto) {		this.fotoobjeto = fotoobjeto;	}	public ObjetoPerdidodata(String nombre, String color, String marca, String lugarpedida, String fotoobjeto) {		this.nombre = nombre;		this.color = color;		this.marca = marca;		this.lugarpedida = lugarpedida;		this.fotoobjeto = fotoobjeto;	}}