package com.pedidos.reservadegrass.data;public class VentasObjeto {	public String getNombre() {		return nombre;	}	public void setNombre(String nombre) {		this.nombre = nombre;	}	public String getDescripcion() {		return descripcion;	}	public void setDescripcion(String descripcion) {		this.descripcion = descripcion;	}	public String getUrl() {		return url;	}	public void setUrl(String url) {		this.url = url;	}	public int getPrecio() {		return precio;	}	public void setPrecio(int precio) {		this.precio = precio;	}	private String nombre;	private String descripcion;	public VentasObjeto(String nombre, String descripcion, String url, int precio) {		this.nombre = nombre;		this.descripcion = descripcion;		this.url = url;		this.precio = precio;	}	private String url;	public VentasObjeto() {	}	private  int precio;}