package com.pedidos.reservadegrass.data;public class VentasObjeto {	public VentasObjeto(String nombre, String descripcion, String color, String marca, int cantidad, int precio) {		this.nombre = nombre;		this.descripcion = descripcion;		this.color = color;		this.marca = marca;		this.cantidad = cantidad;		this.precio = precio;	}	public VentasObjeto() {	}	private String nombre;	private String descripcion;	public String getNombre() {		return nombre;	}	public void setNombre(String nombre) {		this.nombre = nombre;	}	public String getDescripcion() {		return descripcion;	}	public void setDescripcion(String descripcion) {		this.descripcion = descripcion;	}	public String getColor() {		return color;	}	public void setColor(String color) {		this.color = color;	}	public String getMarca() {		return marca;	}	public void setMarca(String marca) {		this.marca = marca;	}	public int getCantidad() {		return cantidad;	}	public void setCantidad(int cantidad) {		this.cantidad = cantidad;	}	public int getPrecio() {		return precio;	}	public void setPrecio(int precio) {		this.precio = precio;	}	private String color;	private String marca;	private int cantidad;	private int precio;}